    @Test
    fun `read best bits of the abi`() {

        // This bit loads up the smart contract structure from the .abi or .json files
        val abiFilenames =
            javaClass.classLoader.getResourceAsStream("abis")?.bufferedReader()?.readLines() ?: emptyList()
        val abiFiles = abiFilenames.mapNotNull {
            javaClass.classLoader.getResourceAsStream("abis/$it")?.bufferedReader()?.readText()
        }

        val objectMapper = jacksonObjectMapper()
            .configure(DeserializationFeature.FAIL_ON_UNKNOWN_PROPERTIES, false)
            .configure(DeserializationFeature.FAIL_ON_IGNORED_PROPERTIES, false)

        val contractFunctions = abiFiles.associate {
            val root = objectMapper.readTree(it)
            val contractName = root.findValue("title") ?: root.findValue("contractName")
            val contractFunctions = objectMapper.readValue<List<AbiContractFunction>>(root.findPath("abi").toString())

            contractName.textValue() to contractFunctions
        }

        println("contracts: \n${contractFunctions.keys}.")
        println("contractFunctions: \n$contractFunctions.")

        // This bit would be setup for a separate worker (outside the sandbox)
        val web3j = initWeb3jConnection(initWeb3jService("http://127.0.0.1:8545"))
        val other = GenericContract(byteCode, contractAddress, web3j, Credentials.create(alicePrivateKey))
        val contract: SmartContract = EVMContract(contractFunctions["Storage"]!!)

        // This call happens inside the flow
        contract.execute("retrieve")
        // execute blocks and sends to the other worker which calls this line
        println("retrieve: ${other.callFunction(outputFunction!!.toContractFunction()).send()}")

        // This call happens inside the flow
        contract.execute(
            "store",
            listOf(
                SmartContract.Input.Builder().name("num").type("uint256").value(15L).build(),
                SmartContract.Input.Builder().name("name").type("tuple").value(
                    listOf(
                        SmartContract.Input.Builder().name("x1").type("uint256").value(1L).build(),
                        SmartContract.Input.Builder().name("struct2").type("tuple").value(
                            listOf(
                                SmartContract.Input.Builder().name("x2").type("uint256").value(2L).build(),
                                SmartContract.Input.Builder().name("struct3").type("tuple").value(
                                    listOf(
                                        SmartContract.Input.Builder().name("x3").type("uint256").value(3L).build(),
                                        SmartContract.Input.Builder().name("struct4").type("tuple").value(
                                            listOf(
                                                SmartContract.Input.Builder().name("x4").type("uint256").value(4L).build()
                                            )
                                        ).build()
                                    )
                                ).build()
                            )
                        ).build()
                    )
                ).build()
            )
        )

        // execute blocks and sends to the other worker which calls this line
        val result = other.callFunction(outputFunction!!.toContractFunction()).send()
        println("store: $result")

//        val myToken: SmartContract = EVMContract(contractFunctions["MyToken"]!!)
