private fun AbiContractFunction.toContractFunction(): ContractFunction {
    return ContractFunction(
        name ?: "",
        this.stateMutability != "view",
        this.inputs.map { it.toContractInput() },
        this.outputs?.map { it.toContractOutput() } ?: emptyList()
    )
}

private fun AbiContractFunctionInput.toContractInput(): SmartContract.Input {
    return SmartContract.Input.Builder().name(this.name).type(this.type).value(this.value).build()
}

private fun AbiContractFunctionInput.toContractOutput(): ContractFunctionOutput {
    return ContractFunctionOutput(this.type)
}

//interface SmartContract {
//    fun execute(functionName: String, parameters: List<AbiContractFunctionInput> = emptyList())
//}
var outputFunction: AbiContractFunction? = null

class EVMContract(
    private val contractFunctions: List<AbiContractFunction>,
) : SmartContract {
    override fun execute(functionName: String) = execute(functionName, emptyList())

    override fun execute(functionName: String, parameters: List<SmartContract.Input>) {
        val function = contractFunctions.find { it.name == functionName }!!.copy()
        outputFunction = if (parameters.isNotEmpty()) {
            val inputs = function.inputs.map { input ->
                parameters.find { it.name == input.name }?.let { input.copy(value = it.value) } ?: input
            }
            function.copy(inputs = inputs)
        } else {
            function
        }

        println("output function: $outputFunction")
    }

}

data class AbiContractFunctionInput(
    val name: String,
    val type: String,
    val internalType: String,
    val value: Any?,
    val components: List<AbiContractFunctionInput>?,
)

data class AbiContractFunction(
    val name: String?,
    val inputs: List<AbiContractFunctionInput>,
    val outputs: List<AbiContractFunctionInput>?,
    val type: String,
    val stateMutability: String?,
)


